import 'package:chat_app/data/services/service_locator.dart';import 'package:chat_app/logic/cubits/auth/auth_cubit.dart';import 'package:chat_app/presentation/screens/login_screen.dart';import 'package:chat_app/router/app_router.dart';import 'package:flutter/material.dart';class HomeScreen extends StatefulWidget {  const HomeScreen({super.key});  @override  State<HomeScreen> createState() => _HomeScreenState();}class _HomeScreenState extends State<HomeScreen> {  @override  Widget build(BuildContext context) {    return Scaffold(      appBar: AppBar(        title: Text("Chats"),        actions: [          IconButton(onPressed: ()async{            await getIt<AuthCubit>().signOut();            getIt<AppRouter>().pushAndRemoveUntil(LoginScreen());          }, icon: Icon(Icons.logout))        ],      ),    );  }}