import 'dart:developer';import 'package:chat_app/config/theme/app_theme.dart';import 'package:chat_app/presentation/screens/sign_up_screen.dart';import 'package:flutter/gestures.dart';import 'package:flutter/material.dart';import '../../core/common/custom_button.dart';import '../../core/common/custom_text_field.dart';class LoginScreen extends StatefulWidget {  const LoginScreen({super.key});  @override  State<LoginScreen> createState() => _LoginScreenState();}class _LoginScreenState extends State<LoginScreen> {  final TextEditingController emailController = TextEditingController();  final TextEditingController passwordController = TextEditingController();  @override  void dispose() {    emailController.dispose();    passwordController.dispose();    super.dispose();  }  @override  Widget build(BuildContext context) {    return Scaffold(      body: SafeArea(        child: Padding(          padding: const EdgeInsets.symmetric(horizontal: 20.0),          child: Form(            child: Column(              crossAxisAlignment: CrossAxisAlignment.start,              children: [                SizedBox(height: 30),                Text(                  "Welcome Back",                  style: Theme.of(context).textTheme.headlineMedium?.copyWith(                    fontWeight: FontWeight.bold,                  ),                ),                SizedBox(height: 10),                Text(                  "Sign in to continue",                  style: Theme.of(                    context,                  ).textTheme.bodyLarge?.copyWith(color: Colors.grey),                ),                SizedBox(height: 30),                CustomTextField(                  controller: emailController,                  hintText: "Enter Email",                  prefixIcon: Icon(Icons.email_outlined),                ),                SizedBox(height: 16),                CustomTextField(                  controller: passwordController,                  hintText: "Enter Password",                  obscureText: true,                  prefixIcon: Icon(Icons.lock_outline),                  suffixIcon: Icon(Icons.visibility),                ),                SizedBox(height: 30),                CustomButton(onPressed: () {}, text: "Login"),                SizedBox(height: 20),                Center(                  child: RichText(                    text: TextSpan(                      text: "Don't have an account?  ",                      style: TextStyle(color: Colors.grey.shade600),                      children: [                        TextSpan(                          text: "Sign up",                          style: Theme.of(                            context,                          ).textTheme.bodyLarge?.copyWith(                            color: AppTheme.primaryColor,                            fontWeight: FontWeight.bold,                          ),                          recognizer: TapGestureRecognizer()..onTap=(){                            Navigator.push(context, MaterialPageRoute(builder: (context) => SignUpScreen()));                          },                        ),                      ],                    ),                  ),                ),              ],            ),          ),        ),      ),    );  }}